mapper.select:
SELECT object_id, site, path, state, system_processing
FROM cstudio_objectstate
WHERE state in

    #{state}

and site=#{site}

SELECT object_id, site, path, state, system_processing
FROM cstudio_objectstate
WHERE site = #{site} AND path = #{path}

mapper.update:
UPDATE cstudio_objectstate
SET system_processing = case when (#{systemProcessing} = TRUE) then 1::bit when (#{systemProcessing} = FALSE) then 0::bit end
WHERE site = #{site} AND path = #{path}

UPDATE cstudio_objectstate
SET system_processing = #{systemProcessing}
WHERE site = #{site} AND path = #{path}

UPDATE cstudio_objectstate
SET system_processing = #{systemProcessing}
WHERE site = #{site} AND path IN

    #{path}

UPDATE cstudio_objectstate
SET state = #{state}, system_processing = #{systemProcessing}::bit
WHERE object_id = #{objectId}

UPDATE cstudio_objectstate
SET state = #{state}, system_processing = #{systemProcessing}
WHERE object_id = #{objectId}

mapper.select:
SELECT object_id, site, path, state, system_processing
FROM cstudio_objectstate
WHERE site = #{site}
AND path in

    #{path}

mapper.update:
UPDATE cstudio_objectstate
SET state = #{state}
WHERE site = #{site}
AND path in

    #{path}

UPDATE cstudio_objectstate
SET path = #{newPath}
WHERE site = #{site}
AND path = #{oldPath}

mapper.delete:
DELETE FROM cstudio_objectstate
WHERE site = #{site}
AND path = #{path}

mapper.select:
SELECT is1.path as path
FROM cstudio_objectstate is1
WHERE is1.site = #{site}
AND is1.path IN

    #{path}

AND is1.state IN

    #{state}

UNION
SELECT is2.path as path
FROM (cstudio_objectstate is2 INNER JOIN cstudio_objectmetadata im2 ON is2.path = im2.path AND is2.site = im2.site)
WHERE is2.site = #{site}
AND im2.renamed > 0
AND is2.path IN

    #{path}

AND is2.state IN

    #{state}
