mapper.select:
SELECT * FROM item WHERE site_id = #{siteId} AND path = #{ldPath}
UNION
(SELECT * FROM item WHERE site_id = #{siteId}
AND path RLIKE('${path}/[^/]+/index\.xml|${path}/(?!index\.xml)[^/]+$')
AND CONCAT(path, '/index.xml') NOT IN
(SELECT i2.path FROM item i2 WHERE i2.site_id = #{siteId}
AND i2.path RLIKE('${path}/[^/]+/index\.xml'))
AND path NOT LIKE '%/${ldName}'

    AND locale_code = #{localeCode}


    AND (path LIKE '%${keyword}%' OR label LIKE '%${keyword}%')

AND ignored = 0


        AND path NOT RLIKE('${exclude}')




        ORDER BY label ${order}, path ${order}


        ORDER BY IF(FIELD(system_type, 'folder')=0,1,0), FIELD(system_type, 'folder'), label ${order}, path ${order}


        ORDER BY LABEL ${order}, path ${order}


LIMIT #{offset},#{limit})

SELECT * FROM item WHERE site_id = #{siteId}
AND path RLIKE('${path}/[^/]+/index\.xml|${path}/(?!index\.xml)[^/]+$')
AND CONCAT(path, '/index.xml') NOT IN
(SELECT i2.path FROM item i2 WHERE i2.site_id = #{siteId}
AND i2.path RLIKE('${path}/[^/]+/index\.xml'))

SELECT COUNT(1) FROM item WHERE site_id = #{siteId}
AND path RLIKE('${path}/[^/]+/index\.xml|${path}/(?!index\.xml)[^/]+$')
AND CONCAT(path, '/index.xml') NOT IN
(SELECT i2.path FROM item i2 WHERE i2.site_id = #{siteId}
AND i2.path RLIKE('${path}/[^/]+/index\.xml'))
AND path NOT LIKE '%/${ldName}'

    AND locale_code = #{localeCode}


    AND (path LIKE '%${keyword}%' OR label LIKE '%${keyword}%')

AND ignored = 0


        AND path NOT RLIKE('${exclude}')

SELECT * FROM item WHERE site_id = #{siteId} AND parent_id = #{parentId} AND path LIKE '%/${ldName}'
UNION
SELECT * FROM item WHERE site_id = #{siteId} AND parent_id = #{parentId} AND path NOT LIKE '%/${ldName}'

    AND locale_code = #{localeCode}


    AND (path LIKE '%${keyword}%' OR label LIKE '%${keyword}%')

AND ignored = 0


        AND path NOT RLIKE('${exclude}')




        ORDER BY label ${order}, path ${order}


        ORDER BY IF(FIELD(system_type, 'folder')=0,1,0), FIELD(system_type, 'folder'), label ${order}, path ${order}


        ORDER BY label ${order}, path ${order}


LIMIT #{offset},#{limit}

SELECT COUNT(1) FROM item WHERE site_id = #{siteId} AND parent_id = #{parentId}
AND path NOT LIKE '%/${ldName}'

    AND locale_code = #{localeCode}


    AND (path LIKE '%${keyword}%' OR label LIKE '%${keyword}%')

AND ignored = 0


        AND path NOT RLIKE('${exclude}')

select i1.*, u1.username as owner, u2.username as creator, u3.username as modifier from item i1 left outer join user u1 on i1.owned_by = u1.id left outer join user u2 on i1.created_by = u2.id left outer join user u3 on i1.last_modified_by = u3.id, item i2 where i2.id in
#{id}
and i1.site_id = i2.site_id and i1.path RLIKE(CONCAT(i2.path, '/index\.xml$')) union select i3.*, u4.username as owner, u5.username as creator, u6.username as modifier from item i3 left outer join user u4 on i3.owned_by = u4.id left outer join user u5 on i3.created_by = u5.id left outer join user u6 on i3.last_modified_by = u6.id where i3.id in
#{id}
and concat(i3.path, '/index.xml') not in (select i4.path from item i4 where i4.site_id = i3.site_id and i4.path RLIKE(CONCAT(i3.path, '/index\.xml$')))

select i1.*, u1.username as owner, u2.username as creator, u3.username as modifier from item i1 left outer join user u1 on i1.owned_by = u1.id left outer join user u2 on i1.created_by = u2.id left outer join user u3 on i1.last_modified_by = u3.id, item i2 where i2.site_id = #{siteId} and i2.path in
#{path}
and i1.ignored = 0 and i1.site_id = i2.site_id and i1.path RLIKE(CONCAT(i2.path, '/index\.xml$')) union select i3.*, u4.username as owner, u5.username as creator, u6.username as modifier from item i3 left outer join user u4 on i3.owned_by = u4.id left outer join user u5 on i3.created_by = u5.id left outer join user u6 on i3.last_modified_by = u6.id where i3.site_id = #{siteId} and i3.path in
#{path}
and i3.ignored = 0 and concat(i3.path, '/index.xml') not in (select i4.path from item i4 where i4.site_id = i3.site_id and i4.path RLIKE(CONCAT(i3.path, '/index\.xml$')))

mapper.update:
UPDATE item SET state = state | #{statesBitMap}
WHERE site_id = #{siteId} AND path IN

    #{path}

UPDATE item SET state = state | #{statesBitMap}
WHERE id IN

    #{id}

UPDATE item SET state = state & ~#{statesBitMap}
WHERE site_id = #{siteId} AND path IN

    #{path}

UPDATE item SET state = state & ~#{statesBitMap}
WHERE id IN

    #{id}

UPDATE item set state = (state | #{onStatesBitMap}) & ~#{offStatesBitMap}
WHERE site_id = #{siteId} AND path IN

    #{path}

UPDATE item set state = (state | #{onStatesBitMap}) & ~#{offStatesBitMap}
WHERE id IN

    #{id}

mapper.select:
select count (1) from item i inner join site s on i.site_id = s.id
where s.site_id = #{siteId}
AND s.deleted = 0
AND i.ignored = 0

    AND i.path RLIKE #{path}


    AND i.last_modified_by in (select id from user where username = #{modifier})



        AND last_modified_on BETWEEN #{dateFrom} AND #{dateTo}


        AND last_modified_on >= #{dateFrom}


        AND #{dateTo} >= last_modified_on

select s.site_id as site_name, u.username as modifier, i.* from item i  inner join site s on i.site_id = s.id
inner join user u on i.last_modified_by = u.id
where s.site_id = #{siteId}
AND s.deleted = 0
AND i.ignored = 0

    AND i.path RLIKE #{path}


    AND u.username = #{modifier}



        AND last_modified_on BETWEEN #{dateFrom} AND #{dateTo}


        AND last_modified_on >= #{dateFrom}


        AND #{dateTo} >= last_modified_on




        ORDER BY ${sort} ${order}


        ORDER BY ${sort}


        ORDER BY last_modified_on ${order}


        ORDER BY last_modified_on DESC


LIMIT #{offset}, #{limit}

mapper.update:
UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET path = #{newPath}
WHERE s.site_id = #{siteId} AND s.deleted = 0 AND i.path = #{oldPath}

mapper.select:
SELECT i3.path as path FROM item i1 inner join site s on i1.site_id = s.id inner join item i3 on i1.parent_id = i3.id WHERE s.site_id = #{siteId} AND s.deleted = 0 AND i1.ignored = 0 AND i1.path IN
#{path}
AND (i1.state & #{newMask}) > 0 UNION SELECT i4.path as path FROM item i2 INNER JOIN site s2 ON i2.site_id = s2.id inner join item i4 on i2.parent_id = i4.id WHERE s2.site_id = #{siteId} AND TRIM(IFNULL(i2.previous_path, '')) <> '' AND i2.ignored = 0 AND i2.path IN
#{path}
AND (i2.state & #{modifiedMask}) > 0

mapper.update:
UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET i.previous_path = NULL
WHERE s.site_id = #{siteId}
AND s.deleted = 0
AND i.path = #{path}

mapper.select:
SELECT count(1)
FROM item i INNER JOIN site s ON i.site_id = s.id
WHERE s.site_id = #{siteId}
AND s.deleted = 0
AND i.ignored = 0
AND i.previous_path = #{previousPath}

mapper.update:
UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET i.commit_id = #{commitId}
WHERE s.site_id = #{siteId}
  AND s.deleted = 0
  AND i.ignored = 0
  AND i.path = #{path}

mapper.select:
SELECT i.path FROM item i INNER JOIN site s ON i.site_id = s.id WHERE s.site_id = #{siteId} AND s.deleted = 0 AND i.ignored = 0 AND i.system_type NOT IN
#{type}
AND (i.path = #{path} or i.path like #{likePath}) AND (i.state & #{inProgressMask}) > 0

mapper.update:
UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET i.last_published_on = #{lastPublishedOn}
WHERE s.site_id = #{siteId}
  AND s.deleted = 0
  AND i.path = #{path}

UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET i.last_published_on = #{lastPublishedOn}
WHERE s.site_id = #{siteId}
  AND s.deleted = 0
  AND i.path IN

    #{path}

UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET i.owned_by = #{lockOwnerId}, i.state = i.state | #{lockedBitOn}
WHERE s.site_id = #{siteId}
AND s.deleted = 0
AND i.path =  #{path}

UPDATE item i INNER JOIN site s ON i.site_id = s.id
SET i.owned_by = null, i.state = i.state & #{lockedBitOff}
WHERE s.site_id = #{siteId}
  AND s.deleted = 0
  AND i.path =  #{path}

UPDATE item i
SET i.owned_by = #{lockOwnerId}, i.state = i.state | #{lockedBitOn}
WHERE i.id =  #{id}

UPDATE item i
SET i.owned_by = null, i.state = i.state & #{lockedBitOff}
WHERE i.id =  #{id}
